package com.learning.basicsofjava;

public class ArithmeticOpr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b = 6;
		int sum = a+b;
		int subtract = a-b;
		int multiply = a*b;
		int divide = a/b;
		int modulus = a%b;
		System.out.println("Addition: "+ sum);
		System.out.println("Subtraction of A and B equals: "+ subtract);
		System.out.println("Multiplication of A and B equals: "+ multiply);
		System.out.println("Division of A and B equals: "+ divide);
		System.out.println("Modulus of A and B equals: "+ modulus);
	}

}
